import React, { useState, useEffect } from 'react';
import { FlatList, StyleSheet, Text, TouchableOpacity, View} from 'react-native';

import ImagemApartamento from './lista-apartamento-imagem/ImagemApartamento';
import { buscarApartamentos } from '../../services/apartamento.service';

import Footer from '../Footer';
import Loading from '../../utils/util';


export default function ListaApartamentos() {
    const [lista, setLista] = useState([]);
    const [ativaLoad, setAtivaLoad] = useState(false);

    useEffect(() => {
        buscarApartamentos().then((retorno) => {
            retorno.dados.then((data) => {
                setLista(data);
            })
        }).catch((error) => {            
            //exibir mensagem na tela dizendo que senha está errado
        })          
      }, [])

    function atualizaOcorrencia(id_apartamento){

        console.log(id_apartamento)

    }
    function ExibeLoad() {
        return (
            <Loading />
        );
    }

    function ExibeDados() {
        return (
            <View style={styles.container}>
                <Text style={{ color: 'white', fontWeight: 'bold', fontSize: 18, padding: 8 }}>Condomínios Vinculados</Text>
                <FlatList
                    data={lista}
                    keyExtractor={item => item.nome}
                    renderItem={({ item }) => (
                    <View style={styles.itemContainer}>
                        <TouchableOpacity style={{ flexDirection: 'column', flex: 1 }}>
                            <View style={{ flexDirection: 'row' }}>
                                <Text style={{ flex: 5, fontWeight: 'bold', color: 'white' }}>{item.nome}</Text>
                            </View>
                            <View style={{ flexDirection: 'row' }}>
                                <Text style={{ flex: 1, color: 'white' }}>{item.endereco}, {item.numeroEndereco}-{item.bairro_logradouro}</Text>
                            </View>
                            <View style={{ flexDirection: 'row' }}>
                                <Text style={{ flex: 1, color: 'white' }}>{item.cep}</Text>
                            </View>
                            <View style={{ flexDirection: 'row' }}>
                                <Text style={{ flex: 1, color: 'white' }}>{item.cidade}, {item.uf}</Text>
                            </View>
                        </TouchableOpacity>
                    </View>
                    )}
                />
                {/* <Footer /> */}
            </View>
        );
    }

    function Exibir(flag) {
        if (flag) {
            return ExibeLoad();
        } else {
            return ExibeDados();
        }
    }

    return (
        <View style={{ flex: 1 }}>
            {Exibir(ativaLoad)}
        </View>
    );

    function exibeImagem(apartamento){
        if(apartamento.alarme){
            return (
                <TouchableOpacity onPress={() => atualizaOcorrencia(apartamento.id)}>
                    <View>
                        <ImagemApartamento apartamento={apartamento} />
                    </View>
                </TouchableOpacity>
            )
        }else{
            return (                
                <View>
                    <ImagemApartamento apartamento={apartamento} />
                </View>                
            )
        }

    }

    return (
        <View style={styles.containerLista}>
            <FlatList
                data={lista}
                keyExtractor={item => item.nome}
                contentContainerStyle={styles.container}
                renderItem={({ item }) => (
                    <View></View>

                )}/>                
            <Footer />
        </View>
    );
}
const styles = StyleSheet.create({
    containerLista: {
        flex: 1
    },
    container: {
        justifyContent: 'space-around',
        flexDirection: 'row',
        flexWrap: 'wrap',
    }
});
