import React from 'react';
import { FlatList, StyleSheet, Text, TouchableOpacity, View } from 'react-native';

import { buscarCondominios } from '../../services/condominio.service';

import Footer from '../Footer';

const dados_covid = require('../../dados/dados_covid.json');

export default function ListaCondominios({ navigation }) {
    const [lista, setLista] = useState();
    var listarCondominios = function () {

        buscarCondominios().then((retorno) => {        
            retorno.dados.then((data) => {                
                setLista(data)                
            })                    
        }).catch((error) => {                        
            //exibir mensagem na tela dizendo que senha est√° errado
        })      

    }



    console.log(lista_condominios)        

    return (
        <View style={styles.container}>
            <FlatList
                data={lista}
                keyExtractor={item => item.nome}
                renderItem={({ item }) => (
                <View style={styles.itemContainer}>
                    <TouchableOpacity
                        style={{ flexDirection: 'column', flex: 1 }}
                        onPress={() => navigation.navigate("Details", { item })}>
                        <View style={{ flexDirection: 'row' }}>
                            <Text style={{ flex: 5, fontWeight: 'bold' }}>{item.nome}</Text>
                            <Text style={{ flex: 1, flexWrap: 'wrap' }}>{item.statuscovid}</Text>
                        </View>
                        <View style={{ flexDirection: 'row' }}>
                            <Text style={{ flex: 1 }}>Sexo:</Text>
                            <Text style={{ flex: 5 }}>{item.sexo}</Text>
                        </View>
                        <View style={{ flexDirection: 'row' }}>
                            <Text style={{ flex: 3 }}>Data de Nascimento:</Text>
                            <Text style={{ flex: 3 }}>{item.data_nasc}</Text>
                        </View>
                    </TouchableOpacity>
                </View>
                )}
            />
            <Footer />
        </View>
    );
}
const styles = StyleSheet.create({
    itemContainer: {
        backgroundColor: '#292E33',        
        borderRadius: 10,        
        margin: 8,
        padding: 8
    },
    text: {
        flexWrap: 'wrap',
        fontFamily: 'Calibri',
        padding: 1
    },
    container:{
        flex:1,
        backgroundColor: '#151A21'
    }
});
