export const logar = async (user, password) => {
    const response = await fetch(
        `http://viacep.com.br/ws/${user}/json`,
    );
    const { logradouro, localidade } = await response.json();    
    return {
        logradouro: logradouro,
        localidade: localidade
    };
};

export const fetchWeather = async woeid => {
    const { logradouro, localidade } = await fetch(
        `https://www.metaweather.com/api/location/${woeid}/`,
    );
    const { title, consolidated_weather } = await response.json();
    const { weather_state_name, the_temp, min_temp, max_temp } = consolidated_weather[0];
    return {
        location: title,
        weather: weather_state_name,
        temperature: the_temp,
        min_temp: min_temp,
        max_temp: max_temp
    };
};
