import React, { useEffect, useState, useReducer } from 'react';
import { FlatList, StyleSheet, Text, TouchableOpacity, View, Image } from 'react-native';
import getImage from '../../utils/Images';

import { buscarCondominios } from '../../services/condominio.service';

import Footer from '../Footer';

const dados_covid = require('../../dados/dados_covid.json');

export default function ListaApartamentos({ navigation }) {
    const [lista, setLista] = useState({});

    var c = 0
    if(c < 1){
        buscarCondominios().then((retorno) => {        
            retorno.dados.then((data) => {                
                setLista(data);                               
            })                    
        }).catch((error) => {                        
            //exibir mensagem na tela dizendo que senha está errado
        })
        c = 2;
    }

    var listarCondominios = function () {


    }



    return (
        <View style={styles.container}>
            <Text style={{ fontWeight: 'bold', fontSize:18, padding:8 }}>Condomínios Vinculados</Text>
            <FlatList
                data={lista}
                keyExtractor={item => item.nome}
                renderItem={({ item }) => (
                <View style={styles.itemContainer}>
                    <TouchableOpacity
                        style={{ flexDirection: 'row', flex: 1 }}>
                        <View style={{ flex:5, flexDirection: 'row', justifyContent: 'space-around', flexWrap: 'wrap'}}>
                            <Image
                                style={styles.image}
                                source={getImage('janela-ok')} />
                            <Image
                                style={styles.image}
                                source={getImage('janela-ok')} />                                
                        </View>

                    </TouchableOpacity>
                </View>
                )}
            />
            <Footer />
        </View>
    );
}
const styles = StyleSheet.create({
    itemContainer: {        
        borderRadius: 10,        
        margin: 8,
        padding: 8
    },
    text: {
        flexWrap: 'wrap',
        fontFamily: 'Calibri',
        padding: 1
    },
    container:{
        flex:1        
    },
    image: {
        width: 150,
        height: 90,
        resizeMode: 'stretch',
        marginBottom: 20

    }
});
