import React, { useEffect, useState, useReducer } from 'react';
import { FlatList, StyleSheet, Text, TouchableOpacity, View, Image } from 'react-native';
import getImage from '../../utils/Images';

import { buscarCondominios } from '../../services/condominio.service';

import Footer from '../Footer';

const dados_covid = require('../../dados/dados_covid.json');

export default function ListaApartamentos({ navigation }) {
    const [lista, setLista] = useState({});

    var c = 0
    if(c < 1){
        buscarCondominios().then((retorno) => {        
            retorno.dados.then((data) => {                
                setLista(data);                               
            })                    
        }).catch((error) => {                        
            //exibir mensagem na tela dizendo que senha est√° errado
        })
        c = 2;
    }

    var listarCondominios = function () {

    }

    function renderImagem(){
        return(
            <View>
                <Image
                    style={styles.image}
                    source={getImage('janela-ok')} />
            </View>
        )
    }

    return (
        <View style={styles.containerLista}>            
            <FlatList
                data={lista}                
                keyExtractor={item => item.nome}
                renderItem={({ item }) => (
                    <View style={{ flex:1,flexDirection: 'row' }} >

                        {renderImagem()}                    
                    </View>


                )}
            />
            <Footer />
        </View>
    );
}
const styles = StyleSheet.create({
    containerLista: {
        flex: 1, 
        flexDirection: 'row',
        justifyContent: 'space-around',        
        flexWrap: 'wrap'
    },
    itemContainer: {        
        borderRadius: 10,        
        margin: 8,
        padding: 8
    },
    text: {
        flexWrap: 'wrap',
        fontFamily: 'Calibri',
        padding: 1
    },
    container:{
        flex:1        
    },
    image: {
        width: 150,
        height: 90,
        resizeMode: 'stretch',
        marginBottom: 20

    }
});
