export function getConnectionAPI(url) {
    return fetch(url)
        .then((response) => response.json())
        .then((responseJson) => {            
            return responseJson;
        })
        .catch((error) => {
            console.error(error);
        });
}

export function postConnectionAPI(url, body) {
    fetch(url, {
        method: 'POST',
        headers: {
          Accept: 'application/json',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(body),
      });
}
