import React, { useEffect, useState, useReducer } from 'react';
import { FlatList, StyleSheet, Text, TouchableOpacity, View, Image } from 'react-native';
import getImage from '../../utils/Images';

import Footer from '../Footer';
import { buscarApartamentos } from '../../services/apartamento.service';

const dados_covid = require('../../dados/dados_covid.json');

export default function ListaApartamentos({ navigation }) {
    const [lista, setLista] = useState({});

    var c = 0
    if (c < 1) {
        buscarApartamentos().then((retorno) => {
            retorno.dados.then((data) => {
                setLista(data);
            })
        }).catch((error) => {
            alert("erro: "+ error)
            //exibir mensagem na tela dizendo que senha est√° errado
        })
        c = 2;
    }

    var listarCondominios = function () {

    }

    function renderImagem(apartamento) {
        return (
            <View>
                {apartamento.alarme && (
                    <View style={{ marginBottom: 20, flex:1, alignItems: 'center' }}>
                        <Image
                            style={styles.image}
                            source={getImage('janela-ok')} />
                        <Text>{apartamento.numero} - {apartamento.bloco}</Text>
                    </View>
                )}

                {!apartamento.alarme && (
                    <View style={{ marginBottom: 20, flex:1, alignItems: 'center' }}>
                        <Image
                            style={styles.image}
                            source={getImage('janela-alarme')} />
                        <Text>{apartamento.numero} - {apartamento.bloco}</Text>
                    </View>
                )}
            </View>
        )
    }

    return (
        <View style={styles.containerLista}>
            <FlatList
                data={lista}
                keyExtractor={item => item.nome}
                contentContainerStyle={styles.container}
                renderItem={({ item }) => (
                    <View>
                        {renderImagem(item)}
                    </View>
                )}/>                
            <Footer />
        </View>
    );
}
const styles = StyleSheet.create({
    containerLista: {
        flex: 1
    },
    container: {
        justifyContent: 'space-around',
        flexDirection: 'row',
        flexWrap: 'wrap',
    },
    image: {
        width: 150,
        height: 90,
        resizeMode: 'stretch',        
    }
});
