import { getConnectionAPI } from "../utils/ConnectionAPI";

export function logar(user, password) {
    return fetch(`http://viacep.com.br/ws/${user}/json`)
        .then((response) => response.json())
        .then((responseJson) => {
            return responseJson;
        })
        .catch((error) => {
            console.error(error);
        });
}

export function getMoviesFromApiAsync(user, password) {
    return fetch(`http://viacep.com.br/ws/${user}/json`)
        .then((response) => response.json())
        .then((responseJson) => {
            return responseJson;
        })
        .catch((error) => {
            console.error(error);
        });
}
