import React, { useState, useEffect } from 'react';
import { FlatList, StyleSheet, Text, TouchableOpacity, View, Image } from 'react-native';
import getImage from '../../utils/Images';
import ImagemApartamento from './lista-apartamento-imagem/ImagemApartamento';

import Footer from '../Footer';
import { buscarApartamentos } from '../../services/apartamento.service';


export default function ListaApartamentos() {
    const [lista, setLista] = useState([]);

    useEffect(() => {
        buscarApartamentos().then((retorno) => {
            retorno.dados.then((data) => {
                setLista(data);
            })
        }).catch((error) => {            
            //exibir mensagem na tela dizendo que senha est√° errado
        })          
      }, [])

    function atualizaOcorrencia(id_apartamento){
        console.log(id_apartamento)

    }

    return (
        <View style={styles.containerLista}>
            <FlatList
                data={lista}
                keyExtractor={item => item.nome}
                contentContainerStyle={styles.container}
                renderItem={({ item }) => (
                    <TouchableOpacity onPress={() => atualizaOcorrencia(item.id)}>
                        <View>
                            <ImagemApartamento apartamento={item} />
                        </View>
                    </TouchableOpacity>
                )}/>                
            <Footer />
        </View>
    );
}
const styles = StyleSheet.create({
    containerLista: {
        flex: 1
    },
    container: {
        justifyContent: 'space-around',
        flexDirection: 'row',
        flexWrap: 'wrap',
    }
});
