import React, { useEffect, useState, useReducer } from 'react';
import { Button, TextInput, ImageBackground, StyleSheet, Text, View, Image } from 'react-native';
import { logar, cadastrar, alterar, deletar } from '../../services/login.service';
import { Icon } from 'react-native-elements'



export default function CadastroUsuario({ navigation }) {

    const [user, setUser] = useState('');
    const [password, setPassword] = useState('');

    function realizarLogin() {
        var usuario = {
            "usuario": user,
            "senha": password
        }            
        logar(usuario).then((retorno) => {
            if(retorno.sts == 200){                    
                retorno.dados.then((dados) => {                
                    navigation.navigate("ListaCondominios", { idUsuario: dados.data.idUsuario });                                                        
                }) 
            }else{
                //exibir mensagem na tela dizendo que senha está errado
                console.log("STATUS: " + retorno.sts)
            }
        }).catch((error) => {                        
            console.log("Erro ao realizar login: " + error)
            navigation.navigate("Erro");            
        })

    }

    //NOME, CPF, CELULAR, LOGIN, SENHA

    return (
        <View style={styles.container}>
            <View style={styles.containerCard}>

                <View style={styles.containerInput}>
                    <Icon
                        name='menu'
                        type='ionicon'
                        color='#ffffff' />
                    <TextInput style={styles.inputStyle}
                        keyboardType="text"
                        onChangeText={setUser}
                        placeholder="usuário"
                        value={user} />
                </View>
            </View>



            <TextInput style={styles.inputStyle}
                keyboardType="password"
                onChangeText={setPassword}
                placeholder="senha"
                secureTextEntry={true}
                value={password} />

            <View style={styles.conteinerButton}>
                <Button style={styles.button}
                    color="#2F2E2E"
                    title="Entrar na minha conta"
                    accessibilityLabel="Botão que realiza o login"
                    onPress={realizarLogin} />
            </View>
        </View>
    );
}
const styles = StyleSheet.create({
    containerInput:{        
        flexDirection: 'row',
        alignItems: 'center'
    },
    containerCard: {
        backgroundColor: '#151A21',
        padding: 30,
        shadowColor: 'white',
        shadowOffset:{
            width: 1,
            height: 1
        },
        shadowOpacity: 0.8,
        shadowRadius: 10

    },
    container: {
        flex: 1,
        backgroundColor: '#151A21',
        opacity: 0.7,
        alignItems: 'center',
        padding: 16
    },
    imageContainer: {
        flex: 1
    },
    inputStyle: {
        backgroundColor: 'white',
        borderColor: 'gray',
        borderRadius: 1,
        borderWidth: 2,
        height: 40,
        marginBottom: 10,
        paddingHorizontal: 5,
        width: 300
    },    
    conteinerButton: {
        flex: 1,
        alignItems: 'center',
        marginTop: 30
    },
});
