import React, { useEffect, useState, useReducer } from 'react';
import { FlatList, StyleSheet, Text, TouchableOpacity, View } from 'react-native';

import { buscarCondominios } from '../../services/condominio.service';

import Footer from '../Footer';

const dados_covid = require('../../dados/dados_covid.json');

export default function ListaCondominios({ navigation }) {
    const [lista, setLista] = useState({});

    function listarCondominios () {

        buscarCondominios().then((retorno) => {        
            retorno.dados.then((data) => {                
                setLista(data);                               
            })                    
        }).catch((error) => {                        
            //exibir mensagem na tela dizendo que senha est√° errado
        })          
    }
    listarCondominios();

    return (
        <View style={styles.container}>
            <FlatList
                data={lista}                
                renderItem={({ condominio }) => (
                <View style={styles.itemContainer}>
                    <TouchableOpacity
                        style={{ flexDirection: 'column', flex: 1 }}
                        onPress={() => navigation.navigate("Details", { condominio })}>
                        <View style={{ flexDirection: 'row' }}>
                            <Text style={{ flex: 5, fontWeight: 'bold' }}>{condominio}</Text>                            
                        </View>
                        <View style={{ flexDirection: 'row' }}>
                            <Text style={{ flex: 2 }}></Text>
                            <Text style={{ flex: 4 }}></Text>
                        </View>
                        <View style={{ flexDirection: 'row' }}>
                            <Text style={{ flex: 3 }}></Text>
                            <Text style={{ flex: 3 }}></Text>
                        </View>
                    </TouchableOpacity>
                </View>
                )}
            />
            <Footer />
        </View>
    );
}
const styles = StyleSheet.create({
    itemContainer: {
        backgroundColor: '#292E33',        
        borderRadius: 10,        
        margin: 8,
        padding: 8
    },
    text: {
        flexWrap: 'wrap',
        fontFamily: 'Calibri',
        padding: 1
    },
    container:{
        flex:1,
        backgroundColor: '#151A21'
    }
});
