import React, { useEffect, useState, useReducer } from 'react';
import { Button, TextInput, ImageBackground, StyleSheet, Text, View, Image } from 'react-native';
import { logar, cadastrar, alterar, deletar } from '../services/login.service';
import { useAsyncStorage } from '@react-native-async-storage/async-storage';
import getImage from '../utils/Images';
import Loading from '../utils/util';

export default function Login({ navigation }) {

    const [user, setUser] = useState('');
    const [password, setPassword] = useState('');    
    const [ativaLoad, setAtivaLoad] = useState(false);
    const [ativaErro, setAtivaErro] = useState(false);
    const { getItem, setItem } = useAsyncStorage('idUsuario')

    function realizarLogin() {

        var usuario = {
            "usuario": user,
            "senha": password
        }            
        if(!camposValidos(usuario)){
            console.log("teste")

            setAtivaErro(true)
            return false;
        }else{
            setAtivaLoad(true)
            logar(usuario).then((retorno) => {                    
                if(retorno.sts == 200){                    
                    retorno.dados.then((dados) => {
                        setAtivaLoad(false)
                        setAtivaErro(false)
                        setItem(dados.data.idUsuario)             
                        navigation.navigate("ListaCondominios", { idUsuario: dados.data.idUsuario });                                                        
                    }) 
                }else{
                    console.log("status> " + retorno.sts)
                    setAtivaLoad(false)
                    setAtivaErro(false)
                    //exibir mensagem na tela dizendo que senha está errado
                    console.log("Erro: " + retorno.sts)
                }
            }).catch((error) => {
                setAtivaLoad(false)
                setAtivaErro(false)
                console.log("Erro ao realizar login: " + error)
                navigation.navigate("Erro");            
            })

        }

    }

    function camposValidos(usuario){
        return usuario.usuario == '' || usuario.senha == '' ? false : true;        
    }

    function ExibirMensagemErro(flag){
        if(flag){
            return (
                <View>
                    <Text style={{fontSize: 20, color: 'white'}}>Por favor, preencha todos os campos!</Text>
                </View>
            )
        }        
    }

    function ExibeLoad(){
        return(
            <Loading/>
        );
    }

    function ExibeDados(){
        return(
            <ImageBackground
            source={getImage('background')}
            imageStyle={styles.imageBackground}
            style={styles.imageContainer}>
            <View style={styles.container}>
                <Image
                    style={styles.image}
                    source={getImage('logo-transparente')} />
                <TextInput style={styles.inputStyle}
                    keyboardType="text"
                    onChangeText={setUser}
                    placeholder="usuário"
                    value={user} />
                <TextInput style={styles.inputStyle}
                    keyboardType="password"
                    onChangeText={setPassword}
                    placeholder="senha"
                    secureTextEntry={true}
                    value={password} />

                {ExibirMensagemErro(ativaErro)}

                <View style={styles.conteinerButton}>
                    <Button style={styles.button}
                        color="#2F2E2E"
                        title="Entrar na minha conta"
                        accessibilityLabel="Botão que realiza o login"
                        onPress={realizarLogin} />
                </View>
            </View>
        </ImageBackground>
        );
    }

    function Exibir(flag){
        if(flag){
            return ExibeLoad();
        }else{
            return ExibeDados();
        }        
    }

    return(
        <View style={{flex: 1}}>
            {Exibir(ativaLoad)}
        </View>        
    );  

}
const styles = StyleSheet.create({
    image: {
        width: 200,
        height: 200,
        resizeMode: 'stretch'
    },
    container: {
        flex: 1,
        backgroundColor: 'black',
        opacity: 0.7,
        alignItems: 'center',
        padding: 16
    },
    imageContainer: {
        flex: 1
    },
    inputStyle: {
        backgroundColor: 'white',
        borderColor: 'gray',
        borderRadius: 1,
        borderWidth: 2,
        height: 40,
        marginBottom: 10,
        paddingHorizontal: 5,
        width: 300
    },
    imageBackground: {
        flex: 1,
        width: null,
        height: null,
        resizeMode: 'cover'
    },
    conteinerButton: {
        flex: 1,
        alignItems: 'center',
        marginTop: 30
    },
});
