import React, { useState, useEffect } from 'react';
import { FlatList, StyleSheet, Text, TouchableOpacity, View } from 'react-native';

import ImagemApartamento from './lista-apartamento-imagem/ImagemApartamento';
import { buscarApartamentos, atualizarOcorrenciaApartamento } from '../../services/apartamento.service';

import Footer from '../Footer';
import Loading from '../../utils/util';


export default function ListaApartamentos() {
    const [lista, setLista] = useState([]);
    const [ativaLoad, setAtivaLoad] = useState(false);

    useEffect(() => {
        buscarApartamentos().then((retorno) => {
            retorno.dados.then((data) => {
                setLista(data);
            })
        }).catch((error) => {
            //exibir mensagem na tela dizendo que senha está errado
        })
    }, [])

    function atualizarOcorrenciaApartamento(id_apartamento) {
        buscarApartamentos().then((retorno) => {
            retorno.dados.then((data) => {
                setLista(data);
            })
        }).catch((error) => {
            //exibir mensagem na tela dizendo que senha está errado
        })


    }
    function ExibeLoad() {
        return (
            <Loading />
        );
    }

    function exibeImagem(apartamento) {
        if (!apartamento.alarme) {
            return (
                <TouchableOpacity onPress={() => atualizaOcorrencia(apartamento.id)}>
                    <View>
                        <ImagemApartamento apartamento={apartamento} />
                    </View>
                </TouchableOpacity>
            )
        } else {
            return (
                <View>
                    <ImagemApartamento apartamento={apartamento} />
                </View>
            )
        }

    }

    function ExibeDados() {
        return (
            <View style={styles.containerLista}>
                <FlatList
                    data={lista}
                    keyExtractor={item => item.nome}
                    contentContainerStyle={styles.container}
                    renderItem={({ item }) => (
                    <View>
                        {exibeImagem(item)}
                    </View>
                    )} />
            </View>
        );
    }


    function Exibir(flag) {
        return flag ? ExibeLoad() : ExibeDados();
    }

    return (
        <View style={{ flex: 1 }}>
            {Exibir(ativaLoad)}
        </View>
    );


}
const styles = StyleSheet.create({
    containerLista: {
        flex: 1
    },
    container: {
        justifyContent: 'space-around',
        flexDirection: 'row',
        flexWrap: 'wrap',
    }
});
