import React, { useEffect, useState, useReducer } from 'react';
import { Button, TextInput, ImageBackground, StyleSheet, Text, View, Image } from 'react-native';
import { logar, cadastrar, alterar, deletar } from '../../services/login.service';
import { Icon } from 'react-native-elements'



export default function CadastroUsuario({ navigation }) {

    const [nome, setNome] = useState('');
    const [cpf, setCpf] = useState('');
    const [celular, setCelular] = useState('');
    const [user, setUser] = useState('');
    const [password, setPassword] = useState('');

    function realizarCadastro() {
        var usuario = {
            "usuario": user,
            "senha": password
        }            
        logar(usuario).then((retorno) => {
            if(retorno.sts == 200){                    
                retorno.dados.then((dados) => {                
                    navigation.navigate("ListaCondominios", { idUsuario: dados.data.idUsuario });                                                        
                }) 
            }else{
                //exibir mensagem na tela dizendo que senha está errado
                console.log("STATUS: " + retorno.sts)
            }
        }).catch((error) => {                        
            console.log("Erro ao realizar login: " + error)
            navigation.navigate("Erro");            
        })

    }

    return (
        <View style={styles.container}>
            <View style={styles.containerCard}>

                <View style={styles.containerInput}>                    
                    <TextInput style={styles.inputStyle}
                        keyboardType="text"
                        onChangeText={setNome}
                        placeholder="Nome"
                        value={nome} />
                </View>
                <View style={styles.containerInput}>                   
                    <TextInput style={styles.inputStyle}
                        keyboardType="text"
                        onChangeText={setCpf}
                        placeholder="CPF"
                        value={cpf} />
                </View>
                <View style={styles.containerInput}>                   
                    <TextInput style={styles.inputStyle}
                        keyboardType="text"
                        onChangeText={setCelular}
                        placeholder="Celular"
                        value={celular} />
                </View>
                <View style={styles.containerInput}>                   
                    <TextInput style={styles.inputStyle}
                        keyboardType="text"
                        onChangeText={setUser}
                        placeholder="Usuário"
                        value={user} />
                </View>
                <View style={styles.containerInput}>                   
                    <TextInput style={styles.inputStyle}
                        keyboardType="text"
                        onChangeText={setPassword}
                        placeholder="Senha"
                        value={password} />
                </View>
                <View style={styles.conteinerButton}>
                    <Button style={styles.button}
                    color="#2F2E2E"
                    title="Cadastrar"
                    accessibilityLabel="Botão que realiza o cadastro de usuário"
                    onPress={realizarCadastro} />
                </View>
            </View>            
        </View>
    );
}
const styles = StyleSheet.create({
    containerInput:{        
        flexDirection: 'row',
        alignItems: 'baseline'
    },
    containerCard: {
        backgroundColor: '#151A21',
        padding: 30,
        shadowColor: 'white',
        shadowOffset:{
            width: 1,
            height: 1
        },
        shadowOpacity: 0.1,
        shadowRadius: 10

    },
    container: {
        flex: 1,
        backgroundColor: '#151A21',        
        alignItems: 'center',
        padding: 16,
        justifyContent: 'center'

    },
    inputStyle: {
        backgroundColor: 'none',
        borderBottomColor: 'gray',
        borderRadius: 1,
        borderBottomWidth: 2,
        height: 40,
        marginBottom: 10,
        paddingHorizontal: 5,
        width: 300
    },    
    conteinerButton: {
        flex: 1,
        alignItems: 'center',
        marginTop: 30,
        borderRadius: 10
    },
});
