import React, { useState, useEffect } from 'react';
import { FlatList, StyleSheet, Text, TouchableOpacity, View, Modal, Pressable, Alert } from 'react-native';

import ImagemApartamento from './lista-apartamento-imagem/ImagemApartamento';
import { buscarApartamentos, atualizarOcorrenciaApartamento } from '../../services/apartamento.service';

import Footer from '../Footer';
import Loading from '../../utils/util';
import ModalConfirmacao from './modal-confirmacao-ocorrencia/Modal'



export default function ListaApartamentos() {
    const [lista, setLista] = useState([]);
    const [ativaLoad, setAtivaLoad] = useState(false);
    const [modalVisible, setModalVisible] = useState(true);

    useEffect(() => {
        buscarApartamentos().then((retorno) => {
            retorno.dados.then((data) => {
                setLista(data);
            })
        }).catch((error) => {
            //exibir mensagem na tela dizendo que senha est√° errado
        })
    }, [])

    function atualizaOcorrencia(id_apartamento) {
        atualizarOcorrenciaApartamento(id_apartamento)       
    }

    function ExibeLoad() {
        return (
            <Loading />
        );
    }

    function exibeImagem(apartamento) {
        if (!apartamento.alarme) {
            return (
                <TouchableOpacity onPress={() => atualizaOcorrencia(apartamento.id)}>
                    <View>
                        <ImagemApartamento apartamento={apartamento} />
                    </View>
                </TouchableOpacity>
            )
        } else {
            return (
                <View>
                    <ImagemApartamento apartamento={apartamento} />
                </View>
            )
        }
    }

    function ExibeDados() {
        return (
            <View style={styles.containerLista}>                
                <FlatList
                    data={lista}
                    keyExtractor={item => item.nome}
                    contentContainerStyle={styles.container}
                    renderItem={({ item }) => (
                        <View>
                            {exibeImagem(item)}
                        </View>
                    )} />
            </View>
        );
    }

    function Exibir(flag) {
        return flag ? ExibeLoad() : ExibeDados();
    }

    return (
        <View>
            <ModalConfirmacao/>
            <View style={{  }}>

                {Exibir(ativaLoad)}            

            </View>
        </View>
    );


}
const styles = StyleSheet.create({
    containerLista: {
        flex: 1,
        marginTop: 20
    },
    container: {
        justifyContent: 'space-around',
        flexDirection: 'row',
        flexWrap: 'wrap',
    },
    centeredView: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
        marginTop: 22
    },
    modalView: {
        margin: 20,
        backgroundColor: "white",
        borderRadius: 20,
        padding: 35,
        alignItems: "center",
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 2
        },
        shadowOpacity: 0.25,
        shadowRadius: 4,
        elevation: 5
    },
    button: {
        borderRadius: 20,
        padding: 10,
        elevation: 2
    },
    buttonOpen: {
        backgroundColor: "#F194FF",
    },
    buttonClose: {
        backgroundColor: "#2196F3",
    },
    textStyle: {
        color: "white",
        fontWeight: "bold",
        textAlign: "center"
    },
    modalText: {
        marginBottom: 15,
        textAlign: "center"
    }
});
